import { createContext, useContext, useReducer, ReactNode } from "react";

type State = {
  currentStep : number;
  name: string;
  level: 0|1;
  email: string;
  github: string
}

type ContextType = {
  state: State;
  dispatch: (action: Action) => void
}

type FormProvideProps = {
  children: ReactNode
}

const initialData: State = {
  currentStep: 0,
  name: '',
  level: 0,
  email: '',
  github:''
}

type Action = {
  type: FormActions;
  payload: any;
}

//context
const FormContext = createContext<ContextType | undefined>(undefined)

//Reducer
export enum FormActions {
  setCurrentStep,
  setName,
  setLevel,
  setEmail,
  setGithub
}

//state recebe os dados e action qual ação executar com os respectiovos dados
const formReducer = (state: State, action: Action) => {
  switch (action.type) {

    case FormActions.setCurrentStep:
      return {...state, currentStep: action.payload}

    case FormActions.setName:
      return {...state, name:action.payload}

    case FormActions.setLevel:
      return {...state, level: action.payload}

    case FormActions.setEmail:
      return {...state, email: action.payload}

    case FormActions.setGithub:
      return {...state, github: action.payload}

    default:
      return state;

  }
}

//provider - utiliza o reducer e produz um ambiente geral para os dados
export const FormProvider = ({children}: FormProvideProps) => {

  const [state, dispatch] = useReducer(formReducer, initialData)
  const value = {state, dispatch}

  return (
    <FormContext.Provider value={value}>
      {children}
    </FormContext.Provider>
  )
}


// context Hook - utilizar e acessar os dados do contexto é utilizaedo o hook
export const useForm = () => {
  const context = useContext(FormContext)
  if (context === undefined) {
    throw new Error('useForm precisa ser usado dentro do FormProvider')
  }
  return context;
}
